#include "gpio.h"

int main(void)
{
    uint8_t currentState = 1;
    uint8_t previousState = 0;

    GPIO_Init();

    while (1)
    {
        currentState = GPIO_ReadSwitch();

        /* Detect rising edge: LOW â†’ HIGH */
        if ((currentState == 1) && (previousState == 0))
        {
            GPIO_ToggleRedLED();
        }

        previousState = currentState;
    }
}
